mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
ORDER BY reg_date DESC

mapper.update:
UPDATE file
   SET parent_table = #{parentTable}
      ,parent_no = #{parentNo}
      ,file_name = #{fileName}
      ,origin_name = #{originName}
      ,file_path = #{filePath}
      ,file_size = #{fileSize}
      ,file_code = #{fileCode}
WHERE no = #{no}

mapper.delete:
DELETE FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
